mapper.select:
{  CALL checkAdmin( 
	#{adminId},
	#{ref_cursor, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=AdminMap}
)  }

{  CALL getAllCount(
		#{adminID},
		#{ref_cursor, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=AdminMap}
	)  }

{  CALL reportList( 
	#{reporter_id},  #{reported_id},  #{post_num}, 	#{story_num},  #{reason}, 	#{type}
	#{ref_cursor, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=AdminMap}
)  }

